mapper.select:
SELECT * FROM mytransaction mt
JOIN usersandmytransaction uat ON uat.trid = mt.trid JOIN userspost up ON up.pid = uat.pid WHERE up.uid = #{uid}
WHERE mt.mOneId = #{mOneId}
JOIN usersandmytransaction uat ON uat.trid = mt.trid JOIN userspost up ON up.pid = uat.pid WHERE up.uid = #{uid} AND mt.mOneId = #{mOneId}
